export function loginRequest(user, password) {
  return {
    type: '@auth/LOGIN_REQUEST',
    payload: { user, password },
  };
}
export function loginSuccess(token, id, admin, nivel) {
  return {
    type: '@auth/LOGIN_SUCCESS',
    payload: { token, id, admin, nivel },
  };
}
export function loginFailure() {
  return {
    type: '@auth/LOGIN_FAILURE',
  };
}
export function createUserRequest(name, email, password, type, cpf) {
  return {
    type: '@auth/CREATE_USER_REQUEST',
    payload: { name, email, password, type, cpf },
  };
}
export function signOut() {
  return {
    type: '@auth/SIGN_OUT',
  };
}
