import styled from 'styled-components';

export const Container = styled.div`
  display: flex;
  flex-direction: column;
  margin-left: 20px;
`;
export const Header = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;

  background: green;
  p {
    color: #fff;
    display: flex;
    align-self: center;
    align-items: center;
  }
`;

export const Body = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  align-self: center;
  width: 40%;
  background: #fff;
`;

export const Title = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
`;
