import React from 'react';
import { useForm } from 'react-hook-form';
import { Container, Body, Info, Logo } from './styles';
import logo from '../../assests/img/logo.png';

export default function Desafio01() {
  const { register, handleSubmit } = useForm();
  function onSubmit(data) {
    console.log(data);
  }
  return (
    <Container>
      <Body>
        <header>
          <Logo src={logo} alt="logo" />
        </header>
        <form onSubmit={handleSubmit(onSubmit)}>
          <h1>Passo 02</h1>
          <h3>Você possui um email comercial para o seu negócio ?</h3>
          <Info>
            <strong>Importante:</strong>
            <p>No futuro, você poderá ter um e-mail com um provedor especial</p>
          </Info>
          <input
            placeholder="Coloque aqui seu email comercial"
            name="email"
            ref={register}
          />
          <button type="submit">SIM</button>
          <div>
            <button type="submit">AJUDA</button>
          </div>
        </form>
      </Body>
    </Container>
  );
}
