import styled from 'styled-components';

export const Container = styled.div`
  display: flex;
  flex-direction: column;
  margin: 10 10px;
`;
export const Header = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: center;
  height: 180px;
  background: green;
  p {
    color: #fff;
    font-size: 44px;
  }
`;

export const Body = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  align-self: center;
  width: 40%;
  background: #fff;
  h1 {
    font-weight: bold;
  }
  p {
    font-size: 25px;
  }
  bu
`;

export const Title = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
`;
