import { takeLatest, call, put, all } from 'redux-saga/effects';
import { toast } from 'react-toastify';
import history from '../../../services/history';
import api from '../../../services/api';
import { loginSuccess, loginFailure } from './actions';

export function* login({ payload }) {
  try {
    const { user, password } = payload;
    const response = yield call(api.post, '/sessions', {
      user,
      password,
    });
    const { token, dados } = response.data;
    api.defaults.headers.Authorization = `Bearer ${token}`;
    yield put(loginSuccess(token, dados));
    const { nivel } = dados;
    if (nivel === 1) {
    }
    switch (nivel) {
      case 0:
        history.push('/desafio-1');
        break;

      case 1:
        return <Redirect to="/desafio-2" />;

      case 2:
        return <Redirect to="/desafio-3" />;

      case 3:
        return <Redirect to="/desafio-4" />;

      case 4:
        return <Redirect to="/desafio-5" />;

      case 5:
        return <Redirect to="/desafio-6" />;

      case 6:
        return <Redirect to="/final" />;

      default:
    }
  } catch (err) {
    toast.error('Falha na autenticação, verifique seus dados');
    yield put(loginFailure());
  }
}
export function* createUser({ payload }) {
  try {
    const { name, email, password, type, cpf } = payload;

    yield call(api.post, 'users', {
      name,
      email,
      password,
      type,
      cpf,
    });

    history.push('/');
  } catch (err) {
    toast.error('Falha no cadastro, verifique os dados !');

    yield put(loginFailure());
  }
}

export function setToken({ payload }) {
  if (!payload) return;
  const { token } = payload.auth;
  if (token) {
    api.defaults.headers.Authorization = `Bearer ${token}`;
  }
}
export function signOut() {
  history.push('/');
}
export default all([
  takeLatest('persist/REHYDRATE', setToken),
  takeLatest('@auth/LOGIN_REQUEST', login),
  takeLatest('@auth/CREATE_USER_REQUEST', createUser),
  takeLatest('@auth/SIGN_OUT', signOut),
]);
