import produce from 'immer';
const INITIAL_STATE = {
  profile: null,
  admin: false,
};
export default function user(state = INITIAL_STATE, action) {
  return produce(state, draft => {
    switch (action.type) {
      case '@auth/LOGIN_SUCCESS': {
        draft.profile = action.payload.id;
        draft.admin = action.payload.admin;
        break;
      }
      case '@auth/SIGN_OUT': {
        draft.profile = null;
        draft.admin = false
        break;
      }
      default:
    }
  });
}
