import styled from 'styled-components';

export const Container = styled.div`
  display: flex;
  flex-direction: column;
  margin: 10 10px;
`;

export const Body = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  align-self: center;
  ${media.lessThan('small')`
  width: 80%;
  `}
  width: 40%;
  background: #fff;
  h1 {
    font-weight: bold;
    text-align: center;
  }
`;
