import styled from 'styled-components';
import { darken } from 'polished';
import media from 'styled-media-query';

export const Container = styled.div`
  display: flex;
  flex-direction: column;
  margin: 10 10px;
`;

export const Body = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  align-self: center;
  ${media.lessThan('small')`
  width: 80%;
  `}
  width: 40%;
  background: #fff;
  h1 {
    font-weight: bold;
    text-align: initial;
  }
  header {
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: center;
    height: 100px;
    background: green;
  }
  form {
    display: flex;
    flex-direction: column;
    background: #82cce8;
    padding: 15px 10px;
    input {
      border: 1;
      border-style: solid;
      border-radius: 4px;
      height: 40px;
      padding: 0 15px;
      margin: 0 0 10px;
    }
    select {
      border: 1;
      border-style: solid;
      border-radius: 4px;
      height: 40px;
      padding: 0 15px;
      margin: 0 0 10px;
    }
  }
`;
export const Logo = styled.img`
  ${media.lessThan('small')`
    width: 270px;
    height: 60px;
`}
  width:100%;
`;
